            gap = '';

            v   = createView('_', {'_': value}).innerHTML;

            return v;
        }

        return JsonView(value);
    },

    normalize: function(value, pretty) {
        return Genghis.JSON.stringify(Genghis.JSON.parse(value), pretty);
    }
};
