    },
    initialize: function() {
        _.bindAll(
            this, 'render', 'updateDocument', 'navigate', 'openEditDialog', 'cancelEdit', 'saveDocument', 'destroy',
            'remove', 'download', 'navigateColl', 'navigateDb', 'navigateId', 'showServerError'
        );

