        }

        if (data.file) {
            this.showServerError("Unexpected property: 'file'");
            return;
        }

        var closeModal      = this.closeModal;
        var showServerError = this.showServerError;
        var docs            = this.collection;
        var uploadUrl       = this.collection.url.replace('.files/documents', '.files/files');
        var reader          = new FileReader();

        reader.onload = function(e) {
            data.file = e.target.result;

            $.ajax({
                type:        'POST',
                url:         uploadUrl,
                data:        JSON.stringify(data),
                contentType: 'application/json'
            })
                .success(function(doc) {
                    docs.add(doc);
                    closeModal();

                    var id = new Genghis.Models.Document(doc).prettyId();
                    app.router.navigate(Genghis.Util.route(docs.url + '/' + id), true);
                })
                .error(function(xhr) {
                    var msg;
                    try {
                        msg = JSON.parse(xhr.responseText).error;
                    } catch (e) {
                        // do nothing
                    }

                    showServerError(msg || 'Error uploading file.');
                });
        };

        reader.readAsDataURL(this.currentFile);
    }
});
;
Genghis.Views.Pagination = Backbone.View.extend({
    template: Genghis.Templates.Pagination,
    events: {
        'click a': 'navigate'
    },
    initialize: function() {
        _.bindAll(this, 'render', 'urlTemplate', 'navigate', 'nextPage', 'prevPage');
        this.model.bind('change', this.render);
        $(document).bind('keyup', 'n', this.nextPage);
        $(document).bind('keyup', 'p', this.prevPage);
    },
    render: function() {
        if (this.model.get('pages') == 1) {
            $(this.el).hide();
        } else {
            var count = 9;
            var half  = Math.ceil(count / 2);
            var page  = this.model.get('page');
            var pages = this.model.get('pages');
            var min   = (page > half) ? Math.max(page - (half - 3), 1) : 1;
            var max   = (pages - page > half) ? Math.min(page + (half - 3), pages) : pages;
            var start = (max == pages) ? Math.max(pages - (count - 3), 1) : min;
            var end   = (min == 1) ? Math.min(start + (count - 3), pages) : max;

            if (end >= pages - 2) {
                end = pages;
            }
            if (start <= 3) {
                start = 1;
            }

            var url = this.urlTemplate;
            $(this.el).html(this.template.render({
                page:     page,
                last:     pages,
                firstUrl: url(1),
                prevUrl:  url(Math.max(1, page - 1)),
                nextUrl:  url(Math.min(page + 1, pages)),
                lastUrl:  url(pages),
                pageUrls: _.range(start, end + 1).map(function(i) {
                    return {
                        index:  i,
                        url:    url(i),
                        active: i === page
                    };
                }),
                isFirst: page === 1,
                isStart: start === 1,
                isEnd:   end >= pages,
                isLast:  page === pages
            })).show();
        }
        return this;
    },
    urlTemplate: function(i) {
        var url    = this.collection.url;
        var chunks = url.split('?');
        var base   = chunks.shift();
        var params = Genghis.Util.parseQuery(chunks.join('?'));
        var extra  = {page: i};

        // TODO: this is ugly. fix it.
        if (params.q) {
            // swap out the query for a pretty one
            extra.q = encodeURIComponent(app.selection.get('query'));
        }

        return base + '?' + Genghis.Util.buildQuery(_.extend(params, extra));
    },
    navigate: function(e) {
        e.preventDefault();
        var url = $(e.target).attr('href');
        if (url) {
            app.router.navigate(Genghis.Util.route(url), true);
        }
    },
    nextPage: function(e) {
        if ($(this.el).is(':visible')) {
            e.preventDefault();
            this.$('li.next a[href]').click();
        }
    },
    prevPage: function(e) {
        if ($(this.el).is(':visible')) {
            e.preventDefault();
            this.$('li.prev a[href]').click();
        }
    }
});
;
Genghis.Views.Search = Backbone.View.extend({
    tagName: 'form',
    className: 'navbar-search form-search control-group',
    template: Genghis.Templates.Search,
    events: {
        'keyup input#navbar-query': 'handleSearchKeyup',
        'click span.grippie':       'toggleExpanded',
        'dragmove span.grippie':    'handleGrippieDrag',
        'click button.cancel':      'collapseSearch',
        'click button.search':      'findDocumentsAdvanced'
    },
    initialize: function() {
        _.bindAll(
            this, 'render', 'updateQuery', 'handleSearchKeyup', 'findDocuments', 'findDocumentsAdvanced', 'focusSearch', 'blurSearch',
            'advancedSearchToQuery', 'queryToAdvancedSearch', 'expandSearch', 'collapseSearch', 'collapseNoFocus', 'toggleExpanded',
            'handleGrippieDrag'
        );

        this.model.bind('change', this.updateQuery);
        this.model.bind('change:collection', this.collapseNoFocus);
    },
    render: function() {
        $(this.el).html(this.template.render({query: this.model.get('query')}));
        $(this.el).submit(function(e) { e.preventDefault(); });

        $(document).bind('keydown', '/', this.focusSearch);

        var wrapper   = $(this.el);
        var resizable = wrapper.find('.well');
        var expand    = this.expandSearch;
        var collapse  = this.collapseSearch;
        $('.grippie', this.el).bind('mousedown', function(e) {
            e.preventDefault();

            var minHeight = 30;
            var maxHeight = Math.min($(window).height() / 2, 350);
            $(document).mousemove(mouseMove).mouseup(mouseUp);

            function mouseMove(e) {
                var mouseY = e.clientY + document.documentElement.scrollTop - wrapper.offset().top;

                if (mouseY >= minHeight && mouseY <= maxHeight) {
                    wrapper.height(mouseY + 'px');
                }

                if (wrapper.hasClass('expanded')) {
                    if (mouseY < minHeight) {
                        collapse();
                    }
                } else {
                    if (mouseY > 100) {
                        expand();
                    }
                }

                return false;
            }

            function mouseUp(e) {
                $(document).unbind('mousemove', mouseMove).unbind('mouseup', mouseUp);
                if (!wrapper.hasClass('expanded')) {
                    collapse();
                }
                e.preventDefault();
            }
        });

        return this;
    },
    updateQuery: function() {
        var q = this.normalizeQuery(this.model.get('query') || this.getDocumentQuery() || '');

        this.$('input#navbar-query').val(q);
    },
    getDocumentQuery: function() {
        var q = this.model.get('document');
        if (typeof q === 'string' && q[0] === '~') {
            q = Genghis.JSON.normalize('{"_id":' + Genghis.Util.base64Decode(q.substr(1)) + '}');
        }

        return q;
    },
    handleSearchKeyup: function(e) {
        $(this.el).removeClass('error');

        if (e.keyCode == 13) {
            e.preventDefault();
            this.findDocuments($(e.target).val());
        } else if (e.keyCode == 27) {
            this.blurSearch();
        }
    },
    findDocuments: function(q) {
        var url = Genghis.Util.route(this.model.currentCollection.url + '/documents');

        q = q.trim();
        if (q.match(/^([a-z\d]+)$/i)) {
            url = url + '/' + q;
        } else {
            try {
                q = Genghis.JSON.normalize(q, false);
            } catch (e) {
                $(this.el).addClass('error');
                return;
            }

            url = url + '?' + Genghis.Util.buildQuery({q: encodeURIComponent(q)});
        }

        app.router.navigate(url, true);
    },
    findDocumentsAdvanced: function(e) {
        this.findDocuments(this.editor.getValue());
        this.collapseSearch();
    },
    focusSearch: function(e) {
        if (this.$('input#navbar-query').is(':visible')) {
            if (e) e.preventDefault();
            this.$('input#navbar-query').focus();
        } else if (this.editor && this.$('.well').is(':visible')) {
            if (e) e.preventDefault();
            this.editor.focus();
        }
    },
    blurSearch: function() {
        this.$('input#navbar-query').blur();
        this.updateQuery();
    },
    normalizeQuery: function(q) {
        q = q.trim();

        if (q !== '') {
            try {
                q = Genghis.JSON.normalize(q, false);
            } catch (e) {
                // do nothing, we'll use the un-normalized version.
            }
        }

        return q.replace(/^\{\s*\}$/, '')
                .replace(/^\{\s*(['"]?)_id\1\s*:\s*\{\s*(['"]?)\$id\2\s*:\s*(["'])([a-z\d]+)\3\s*\}\s*\}$/, '$4')
                .replace(/^\{\s*(['"]?)_id\1\s*:\s*(new\s+)?ObjectId\s*\(\s*(["'])([a-z\d]+)\3\s*\)\s*\}$/, '$4');
    },
    advancedSearchToQuery: function() {
        this.$('input#navbar-query').val(this.normalizeQuery(this.editor.getValue()));
    },
    queryToAdvancedSearch: function() {
        var q = this.$('input#navbar-query').val().trim();

        if (q.match(/^[a-z\d]+$/i)) {
            q = '{_id:ObjectId("'+q+'")}';
        }

        if (q !== '') {
            try {
                q = Genghis.JSON.normalize(q, true);
            } catch (e) {
                // Do nothing, we'll copy over the broken JSON
            }
        }

        this.editor.setValue(q);
    },
    expandSearch: function(expand) {
        if (!this.editor) {
            var wrapper = $('.search-advanced', this.el);
            this.editor = CodeMirror($('.well', this.el)[0], _.extend(Genghis.defaults.codeMirror, {
                lineNumbers: false,
                onFocus: function() { wrapper.addClass('focused');    },
                onBlur:  function() { wrapper.removeClass('focused'); },
                extraKeys: {
                    'Ctrl-Enter': this.findDocumentsAdvanced,
                    'Cmd-Enter':  this.findDocumentsAdvanced,
                    'Esc':        this.findDocumentsAdvanced
                 },
                 onChange:        this.advancedSearchToQuery
            }));
        }

        this.queryToAdvancedSearch();

        $(this.el).addClass('expanded');

        var editor      = this.editor;
        var focusSearch = this.focusSearch;
        _.defer(function() {
            editor.refresh();
            focusSearch();
        });
    },
    collapseSearch: function() {
        this.collapseNoFocus();
        this.focusSearch();
    },
    collapseNoFocus: function() {
        $(this.el).removeClass('expanded').css('height', 'auto');
    },
    toggleExpanded: function() {
        if ($(this.el).hasClass('expanded')) {
            this.collapseSearch();
        } else {
            this.expandSearch();
            $(this.el).height(Math.floor($(window).height() / 4) + 'px');
        }
    },
    handleGrippieDrag: function(e) {
        console.log(e);
    }
});
;
